import React, { Component } from 'react'
import Link from 'next/link'

class Header extends Component {
  constructor () {
    super()
    this.state = {
      error: null,
      isLoaded: false,
      user: []
    }
  }
  componentDidMount () {
    var request = new XMLHttpRequest();

    request.open('GET', 'https://coding-challenge-api.aerolab.co/user/me');

    request.setRequestHeader('Content-Type', 'application/json');
    request.setRequestHeader('Accept', 'application/json');
    request.setRequestHeader('Authorization', 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZDFkMmY4NzM2ZjEyNTAwNmZmOWIzZTAiLCJpYXQiOjE1NjIxOTM3OTl9.QaUDZ3qJA5FMwz0ThhGWoIxJ8pFpZv9I00XZM40PORc');

    request.onreadystatechange = function () {
      if (this.readyState === 4) {
        console.log('Status:', this.status);
        console.log('Headers:', this.getAllResponseHeaders());
        console.log('Body:', this.responseText);
        this.setState({
          isLoaded: true,
          user: this.responseText
        });
      }
    };

    request.send();
  }

  render () {
    return ( 
      <header className="header">
      <div>
          <div className="header__logo">
            <img src="./static/img/logo.svg" alt="Aerolab" />
          </div>
          <div className="header__user">
              <div className="header__user__name">{user.name}</div>
              <div className="header__user__points">6000</div>
          </div>
      </div>
      <style jsx>{`
        .header{
          width: 100%;
        }
        .header>div{
          width: 100%;
          height: 80px;
          border-bottom: 1px solid black;
          display: flex;
          justify-content: space-between;
          align-items: center;
          box-sizing: border-box;
          padding: 10px;
        }
        .header__logo img{
          height: 36px;
        }
        `}</style>
    </header>
    )
  }
}

export default Header
