import React, { Component } from 'react'
import Link from 'next/link'

class Header extends Component {
  constructor () {
    super()
    this.state = {
      error: null,
      isLoaded: false,
      user: []
    }
  }
  componentDidMount () {
    var url = "https://coding-challenge-api.aerolab.co/user/me";
    var bearer = 'Bearer ' + eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZDFkMmY4NzM2ZjEyNTAwNmZmOWIzZTAiLCJpYXQiOjE1NjIxOTM3OTl9.QaUDZ3qJA5FMwz0ThhGWoIxJ8pFpZv9I00XZM40PORc;
    fetch(url, {
            method: 'GET',
            withCredentials: true,
            credentials: 'include',
            headers: {
                'Authorization': bearer,
                'X-FP-API-KEY': 'iphone', //it can be iPhone or your any other attribute
                'Content-Type': 'application/json'
            }
        }).then(responseJson => {
            var items = JSON.parse(responseJson._bodyInit);
        })
        .catch(error => this.setState({
            isLoading: false,
            message: 'Something bad happened ' + error
        }));

  }

  render () {
    return ( 
      <header className="header">
      <div>
          <div className="header__logo">
            <img src="./static/img/logo.svg" alt="Aerolab" />
          </div>
          <div className="header__user">
              <div className="header__user__name"></div>
              <div className="header__user__points">6000</div>
          </div>
      </div>
      <style jsx>{`
        .header{
          width: 100%;
        }
        .header>div{
          width: 100%;
          height: 80px;
          border-bottom: 1px solid black;
          display: flex;
          justify-content: space-between;
          align-items: center;
          box-sizing: border-box;
          padding: 10px;
        }
        .header__logo img{
          height: 36px;
        }
        `}</style>
    </header>
    )
  }
}

export default Header
