import React, { Component } from 'react'
import Head from 'Head'
import Link from 'next/link'

class Header extends Component {
  constructor (props) {
    super(props)
    this.state = {
     loading: true, 
     //Ver si puedo agrupar name y points en un string user[]
     name: null,
     points: null,
    }
  }
  async componentDidMount() {
    const response = await fetch('https://coding-challenge-api.aerolab.co/user/me', {
         headers: new Headers({
         Authorization: "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZDFkMmY4NzM2ZjEyNTAwNmZmOWIzZTAiLCJpYXQiOjE1NjIxOTM3OTl9.QaUDZ3qJA5FMwz0ThhGWoIxJ8pFpZv9I00XZM40PORc",
      })
    })
    const data = await response.json();
    this.setState({ name: data.name, points: data.points, loading: false });


  };

  render () {
    return ( 
      <header className="header">
      <div>
          <div className="header__logo">
            <img src="./static/img/logo.svg" alt="Aerolab" />
          </div>
          <div className="header__user">
              <div className="header__user__name">{this.state.name}</div>
              <div className="header__user__points">{this.state.points}</div>
          </div>
      </div>
      <style jsx>{`
        .header{
          width: 100%;
        }
        .header>div{
          width: 100%;
          height: 80px;
          border-bottom: 1px solid black;
          display: flex;
          justify-content: space-between;
          align-items: center;
          box-sizing: border-box;
          padding: 10px;
        }
        .header__logo img{
          height: 36px;
        }
        `}</style>
    </header>
    )
  }
}

export default Header
